import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    firstStep: '',
    piece:'',
    secondStep: '',
}

export const ChessReducer = createSlice({
    name:'lastMove',
    initialState,
    reducers: {
        Step: (state, action)=>{ 
            if(state.firstStep==''&&state.secondStep==''){ 
                state.firstStep = action.step }
            else if(state.firstStep!=''&&state.secondStep==''){ 
                state.secondStep = action.step
                //Chiama la funzione dello smart contract. 
            }
        },
    }
});

export const chessboardReducer = ChessReducer.reducer;
export const {firstStep, secondStep} = ChessReducer.actions;
