import { useMoralis } from "react-moralis";
import React, { useEffect } from 'react';

export default function BtnConnect({ id, names, href, img, classes, current }) {
    const { authenticate, isAuthenticated, isAuthenticating, user, account, logout } = useMoralis();

    useEffect(() => {
    if (isAuthenticated) {
      document.getElementById('btn-connect-header').innerHtml=names[1]
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isAuthenticated]);

    const login = async () => {
      if (!isAuthenticated) {

        await authenticate({signingMessage: "Log in using Moralis" })
          .then(function (user) {
            console.log("logged in user:", user);
            console.log(user.get("ethAddress"));
          })
          .catch(function (error) {
            console.log(error);
          });
      }
    }

    const logOut = async () => {
      await logout();
      console.log("logged out");
    }

    return (
        <div className={"Btn inline-block mr-2 mt-2 bg-rose-400 p-1 text-gray-200 rounded border-2 border-solid border-red-600 mb-2"+classes}>
            <a id="btn-connect-header" className="Connect-Link" href="">{names[0]}</a>
            <img className="" src={img}></img>
        </div>
    );
}
