import React, { Component } from 'react';
import BoxBtn from './BoxBtn';

export default class Box extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        return(
            <div 
                id={this.props.id}
                className={(this.props.color)?'bg-red-500 Box w-12 h-12 text-center ':'bg-amber-800 Box w-12 h-12 text-center'+(this.props.team)?'':''} > 
                {this.props.p}
            </div>
        );
    }
}
