import React, { Component } from 'react';
import Box from './Box'

export default class Col extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        const p = ['01','11','21','31','41','51','61','71','06','16','26','36','46','56','66','76']
        const t = ['00','70','07','77']
        const c = ['10','60','17','67']
        const a = ['20','50','27','57']
        const q = ['30','37']
        const k = ['40','47']
        let col = [];
        for(let i=0; i<8; i++){col[i]=i;}
        return(
            <div className="Col inline-block">
            {col.map((y)=>{


                console.log('id: ',this.props.x);
                console.log('y: ', y);
                let team;
                if(this.props.y==0||this.props.y==1) team=false
                if(this.props.y==6||this.props.y==7) team=true

                if(this.props.x%2==0&&y%2!=0) return(<Box key={String(this.props.x)+String(y) } coo={String(this.props.x)+String(y) } team={team} color={'bg-red-800'}/>);
                else if(this.props.x%2==0&&y%2==0) return(<Box key={String(this.props.x)+String(y) } coo={String(this.props.x)+String(y) } team={team} color={'bg-amber-800'}/>);
                //INSERIRE DISCRIMINANTE PER IL TEAM
                if(this.props.x%2!=0&&y%2!=0) return(<Box key={String(this.props.x)+String(y) } coo={String(this.props.x)+String(y) } team={team} color={'bg-amber-800'}/>);
                else if(this.props.x%2!=0&&y%2==0) return(<Box key={String(this.props.x)+String(y) } coo={String(this.props.x)+String(y) } team={team} color={'bg-red-800'}/>);
            })}
            </div>
        );
    }
}
