import React, { Component } from 'react';
import BoxBtn from './BoxBtn';

export default class Box extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        const p = ['01','11','21','31','41','51','61','71','06','16','26','36','46','56','66','76']
        const t = ['00','70','07','77']
        const c = ['10','60','17','67']
        const a = ['20','50','27','57']
        const q = ['30','37']
        const k = ['40','47']

        return(
            <div 
                id={this.props.id}
                className={"Box w-12 h-12 text-center "+this.props.color} > 
                {p.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn /> }
                })}
                {t.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn /> }
                })}
                {c.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn /> }
                })}
                {a.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn /> }
                })}
                {q.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn /> }
                })}
                {k.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn /> }
                })}
            </div>
        );
    }
}
