import React, { Component } from 'react';
import BoxBtn from './BoxBtn';

export default class Box extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        return(
            <div 
                id={this.props.id}
                className={"Box w-12 h-12 text-center "+this.props.color} > 
                {p.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn p={'p'} /> }
                })}
                {t.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn p={'t'} /> }
                })}
                {c.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn p={'c'} /> }
                })}
                {a.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn p={'a'} /> }
                })}
                {q.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn p={'q'} /> }
                })}
                {k.map((coo)=>{
                    if(coo==this.props.coo){ return <BoxBtn p={'k'} /> }
                })}
            </div>
        );
    }
}
