import React, { Component } from 'react';
import Box from './Box';

export default class Chessboard extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        let chessboard = [[{color:''}]];
        for(let i=0; i<8; i++){
            for(let j=0; j<8; j++){
                if(i%2==0){ chessboard[i][j].color=true;} 
                else { chessboard[i][j].color=false }
            }
        }


        return(
            <div className="Chessboard w-2/3 h-6 inline-block bg-red-600 mt-4">

            {
            chessboard.map( 
               (row)=>{
                   row.map(
                       (box)=>{
                        console.log(box);
                        return(<Box color={box.color}/>)
                    })

                })
            }
            </div>
        );
    }
}
