import React, { Component } from 'react';
import Box from './Box';

export default class Chessboard extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        let chessboard = [[]];
        for ( let y = 0; y < 8; y++ ) {
            chessboard[ y ] = [];
            for ( let x = 0; x < 8; x++ ) {
                if(x%2!=0) chessboard[ y ][ x ] = {color:false};
                else chessboard[ y ][ x ] = {color:true}
            }
        }


        return(
            <div className="Chessboard w-2/3 h-6 inline-block bg-red-600 mt-4">

            {
            chessboard.map( 
               (row)=>{
                   row.map(
                       (box)=>{
                        console.log(box);
                        return(<Box color={box.color}/>)
                    })

                })
            }
            </div>
        );
    }
}
