import { configureStore } from '@reduxjs/toolkit';
import { userReducer } from '../features/userSlice';
import { lastMoveReducer } from '../features/moveSlice';
import { createStore } from 'redux'

const LOGIN = "LOGIN";
const LOGOUT = "LOGOUT";

const initialState = {
  chess:{
    chessboard:[[{ coo:'' }]],
    lastMove: { 
      firstStep:'', 
      piece:'', 
      secondMove:'' 
    },
    graveyard: [{
      white:[{piece:''}],
      black:[{piece:''}]
    }]
  },
  user:{id:'', address:''}
}

function rootReducer(state=initialState, action){
  switch(action.type){
    case LOGIN: 
      state.user.id=action.payloaaf.id;state.user.address=action.payloaaf.address;
      break;
    case LOGOUT: 
      state.user.id='';state.user.address=''
      break;
  }
}

export const store = createStore(rootReducer);
