import React, { Component } from 'react';
import Box from './Box'

export default class Col extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        let col = [];
        for(let i=0; i<8; i++){col[i]=i;}
        return(
            <div className="Col inline-block">
            {col.map((box)=>{
                console.log('id: ',this.props.key);
                console.log('box: ', box);
                if(this.props.key%2==0&&box%2!=0) return(<Box key={String(this.props.key)+String(box) } color={'bg-amber-800'}/>);
                else if(this.props.key%2==0&&box%2==0) return(<Box key={String(this.props.key)+String(box) } color={'bg-amber-800'}/>);

                if(this.props.key%2!=0&&box%2!=0) return(<Box key={String(this.props.key)+String(box) } color={'bg-red-800'}/>);
                else if(this.props.key%2!=0&&box%2==0) return(<Box key={String(this.props.key)+String(box) } color={'bg-red-800'}/>);
            })}
            </div>
        );
    }
}
