import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    firstStep: '',
    piece:'',
    secondStep: '',
}

export const ChessReducer = createSlice({
    name:'chessboard',
    initialState,
    reducers: {
        firstStep: (state, action)=>{ 
            state.firstStep = action.firstStep
            state.piece = action.piece
        },
        secondStep: (state, action)=>{ 
            state.secondStep = action.secondStep
            state.piece = action.piece
        }
    }
});

export const chessboardReducer = ChessReducer.reducer;
export const {firstStep, secondStep} = chessboardReducer.actions;
