import { createStore } from '@reduxjs/toolkit';


const initialState = {
  user:{id:'', address:''},
  lastMove:{
    firstStep:'',
    piece:'',
    lastStep:''
  },
  chessboard:{}
}

function rootReducer(state=initialState, action){
  switch(action.type){
    case "LOGIN":
        state.user.id=action.id;
        state.user.address=action.address;
        break;
    case "LOGOUT":
        state.user.id=action.id;
        state.user.address=action.address;
        break;
    }

}
