import React, { Component } from 'react';

export default class Box extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        const pieces = [{'p':['10','11','12','13','14','15','16','17','60','6','62','63','64','65','66','67'],
                         't':['00','07','70','77'],
                         'c':['01','06','71','76'],
                         'a':['02','05','72','75'],
                         'q':['03','74'],
                         'k':['04','75'],
                        }]
        return(
            <div 
                id={this.props.id}
                className={"Box w-12 h-12 text-center "+this.props.color} > 
                {pieces.p.map((coo)=>{
                    if(coo==this.props.coo){ return'p' }
                })}
                {pieces.p.map((coo)=>{
                    if(coo==this.props.coo){ return't' }
                })}
                {pieces.p.map((coo)=>{
                    if(coo==this.props.coo){ return'c' }
                })}
                {pieces.p.map((coo)=>{
                    if(coo==this.props.coo){ return'a' }
                })}
                {pieces.p.map((coo)=>{
                    if(coo==this.props.coo){ return'q' }
                })}
                {pieces.p.map((coo)=>{
                    if(coo==this.props.coo){ return'k' }
                })}
            </div>
        );
    }
}
