import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

const gameState = {
    chessboard: [[{ piece:"", team:"", color:"" }]],
    lastMove:{firstStep:"", piece:"", secondMove:""},
    graveyard: [[{ piece: "", team:"" }]],
}   

export const gameSlice = createSlice({ 
    name:"game",
    reducers:{
        move: (state, action) => {
            return state;
        },
        lastMove: (state, action) => {
            if(state.game.lastMove.firstStep=='') state.game.lastMove.firstStep=action.payload.coo
            return state;
        }
    },
    initialState: gameState,
})

export const { move, lastMove } = gameSlice.actions;
export const gameReducer = gameSlice.reducer;
