import { useMoralis } from "react-moralis";
import React, { useEffect } from 'react';

export default function BtnConnect({ id, names, img, classes }) {
    const { authenticate, isAuthenticated, isAuthenticating, user, account, logout } = useMoralis();
    const login = async () => {
        if (!isAuthenticated) {

          await authenticate({signingMessage: "Log in using Moralis" })
            .then(function (user) {
              console.log("logged in user:", user);
              console.log(user.get("ethAddress"));
            })
            .catch(function (error) {
              console.log(error);
            });
        }
      }

    const logOut = async () => {
        await logout();
        console.log("logged out");
    }

    useEffect(() => {
        if (isAuthenticated) {
            return (
                <div className={"Btn inline-block mr-2 mt-2 bg-rose-400 p-1 text-gray-200 rounded border-2 border-solid border-red-600 mb-2"+classes}>
                    <a className="">{names[1]}</a>
                    <img className="" src={img}></img>
                </div>
            );
        }
        return (
            <div className={"Btn inline-block mr-2 mt-2 bg-rose-400 p-1 text-gray-200 rounded border-2 border-solid border-red-600 mb-2"+classes}>
                <a className="">{names[0]}</a>
                <img className="" src={img}></img>
            </div>
        );

      }, [isAuthenticated]);
}
