import React from 'react';
import { useMoralis } from 'react-moralis';
import detectEthereumProvider from '@metamask/detect-provider'
const provider = await detectEthereumProvider();


function ConnectBtnMoralis() {
    const { authenticate, isAuthenticated, logout } = useMoralis();

    const logIn = async () => {
        if(provider){
            console.log
            if(!isAuthenticated){
                await authenticate({signingMessage: "Welcome to Dezy"})
                    .then(function(user){
                        console.log("logged in user: ", user)
                        console.log(user.get("ethAddress"))
                    })
                    .catch(function (err){
                        console.log(err)
                    })
            }
        } else {
            console.log('Please install metamask')
        }
    }
    const logOut = async () => {
        await logout();
        console.log("logged out");
    }

    return(
        isAuthenticated 
            ? ''
            : <button onClick={logIn} className="border-2 border-solid border-red-900 hover:border-red-700 rounded-md bg-rose-200 hover:bg-rose-700 text-rose-700 hover:text-white text-sm font-bold p-1">Connect</button>
    );
}

export default ConnectBtnMoralis
